            {user?.role === roles.QA_COORDINATOR && (
              <li className="w-full">
                <Link
                  to="/statistics"
                  className={`flex items-center p-2 text-base justify-between font-normal ${
                    location.pathname === "/statistics"
                      ? "bg-gray-700 dark:bg-gray-900"
